package Week2MethodPracticeProblems;

public class methodPractice5 {
/*	Create a method named "doubleIt" to calculate and return an integer number doubled. For example. doubleIt(7) should return 14. 
*/
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(doubleIt(7));
	}

	private static int doubleIt(int x) {
		return x*2;
	}
}
